const Signup = (props) => {

  const [signUp, setsignUp] = useState(
    {
     username: '',
     password: '',
   }
  );

  const handleInput = e => {
    setsignUp({
        ...signUp,
        [e.target.name]: e.target.value
    });
    console.log(signUp)
  };

  const handelSignUp = e => {
    e.preventDefault();
    axios
      .post("https://airbnb-bw.herokuapp.com/api/register", signUp)
      .then(res => {
        console.log(res);
        props.history.push("/login");
      })
      .catch(err =>
        console.log(
          err.message
        )
      );
  };

    return (
    <Wrapper>
        <SecondWrap>
          <h1>Sign Up</h1>
        <Form onSubmit={handelSignUp}>
          <Input
              type="text"
              name="username"
              placeholder="username"
              value={signUp.username}
              onChange={handleInput}
              required
            />
            <Input
              type="password"
              name="password"
              placeholder="password"
              value={signUp.password}
              onChange={handleInput}
              required
              />
          <Button onClick={e => {e.target.style.background = "#FEBDC0";}}
          onMouseOver={e => {e.target.style.cursor = "pointer";}}>Create Account</Button>
      </Form>
      <Span>Already have an LaOpt account? <Link to="/login"><Login>Log In</Login></Link> </Span>
        </SecondWrap>
    </Wrapper>
    )
}

export default Signup;
